import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function ViewTournament() {
    const [tournaments, setTournaments] = useState([]);

    useEffect(() => {
        const fetchTournaments = async () => {
            try {
                const response = await axios.get('http://localhost:8080/tournaments');
                setTournaments(response.data);
            } catch (error) {
                alert("Something went wrong while fetching tournaments.");
                console.error(error);
            }
        };

        fetchTournaments();
    }, []);

    return (
        <div className="container">
            <h1>List of Tournaments</h1>
            <div className="list-group">
                {tournaments.map(tournament => (
                    <a
                        key={tournament.id}
                        href="#"
                        className={`list-group-item list-group-item-action ${tournament.disabled ? 'disabled' : ''}`}
                        aria-current={tournament.active ? 'true' : undefined}
                        tabIndex={tournament.disabled ? '-1' : undefined}
                        aria-disabled={tournament.disabled ? 'true' : undefined}
                    >
                        {tournament.name}
                    </a>
                ))}
            </div>
        </div>
    );
}

export default ViewTournament;
